import React, { Component } from "react";
import Container from "../components/Container";
import Form from "../components/Form";
import Jumbotron from "../components/Jumbotron";
import Nav from "../components/Nav";
import Results from "../components/Results-container";
import Button from "../components/Button"
import API from "../utils/API";


class Search extends Component {
  state = {
    results: [],
    search: ""
  }

  search = query => {
    API.search(query)
      .then(res =>
        this.setState({ results: res.data.items })
      )
      .catch(err => console.log(err));
  };
  handleInputChange = event => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
      [name]: value
    });
  };
  save = (data) => {
    API.saveBook(data)
  }

  // When the form is submitted, search the OMDB API for the value of `this.state.search`
  handleFormSubmit = event => {
    event.preventDefault();
    this.search(this.state.search);
  };

  render() {
    return (
      <div>
        <Nav />
        <Container>
          <Jumbotron />
          <Form
            onClick={this.handleFormSubmit}
            onChange={this.handleInputChange}
            value={this.state.search}
            name="search"
          />
        </Container>
        {this.state.results.map(res => {
          return (
            <div>
              <Results
                key={res.id}
                imageLinks={res.volumeInfo.imageLinks.thumbnail ? res.volumeInfo.imageLinks.thumbnail : "http://placekitten.com/200/300"}
                title={res.volumeInfo.title ? res.volumeInfo.title : "No title to display"}
                authors={res.volumeInfo.authors ? res.volumeInfo.authors : "No authors"}
                categories={res.volumeInfo.categories ? res.volumeInfo.categories : "No info"}
                description={res.volumeInfo.description ? res.volumeInfo.description : 'No info'}
                onClick={this.save}
              />
              <Button onClick={this.save({
                title:res.volumeInfo.title,
                author:res.volumeInfo.authors,
                synopsis: res.volumeInfo.description
              })} />
            </div>
          )
        })}

      </div>
    )
  }
}

export default Search;
