import React, { Component } from "react";
import Container from "../components/Container";
import Form from "../components/Form";
import Jumbotron from "../components/Jumbotron";
import Nav from "../components/Nav";
import Results from "../components/Results-container";
import API from "../utils/API";
import axios from "axios"


class Search extends Component {
  state = {
    result: {},
    search: ""
  }

  componentDidMount() {
    this.search("https://www.googleapis.com/books/v1/volumes?q=storm+of+swords:keyes&key=AIzaSyBwOemITo_zIAv350jNoazbI_BISg2-nb8");
  }


  search = query => {

    API.search(query)
      .then(res =>
        res.data.items.map(res => {
          this.setState({ result: res.volumeInfo })
        }))
      .catch(err => console.log(err));
  };
  handleInputChange = event => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
      [name]: value
    });
  };

  // When the form is submitted, search the OMDB API for the value of `this.state.search`
  handleFormSubmit = event => {
    event.preventDefault();
    this.search(this.state.search);
  };

  render() {
    return (
      <div>
        <Nav />
        <Container>
          <Jumbotron />
          <Form
            onClick={this.handleFormSubmit}
            onChange={this.handleInputChange}
            value={this.state.search}
            name="search"
          />
          <Results 
          imageLinks={this.state.imageLinks}
          title={this.state.title}
          authors={this.state.authors}
          categories={this.state.categories}
          description={this.state.description}
          />
        </Container>
      </div>
    )
  }
}

export default Search;
